// Pseudo Number Generator //
// ----------------------- //

unsigned int seed = 72727;  // You can set this to any starting value

// Function to generate a pseudo-random number
unsigned int pseudo_random() {
    // LCG parameters (from Numerical Recipes)
    seed = (1103515245 * seed + 12345) & 0x7fffffff;
    return seed;
}

// Function to get a pseudo-random number within a specific range [min, max]
unsigned int random_in_range(int min, int max) {
    return (pseudo_random() % (max - min + 1)) + min;
}

// Start and End Game Subroutines//
void VGA_fill(){
   for (int y = 0; y < SCREEN_HEIGHT; y++) {
        for (int x = 0; x < SCREEN_WIDTH; x++) {
            VGA_draw_point(x, y, BACKGROUND);
        }
    }
}

void draw_character(int x){
    int idx = 0;
    for (int y = 192; y < 240; y++) {
        for (int x = 0; x < 64; x++) {
            short c = amogus[idx++];
            int xpos = x + POSITION * 64;
            VGA_draw_point(xpos, y, c);
        }
    }
}

void erase_character(){
    for (int y = 192; y < 240; y++) {
        for (int x = 0; x < 64; x++) {
            short c = BACKGROUND;
            int xpos = x + POSITION * 64;
            VGA_draw_point(xpos, y, c);
        }
    }
}

